mapper.select:
select bbsno, wname, title, grpno, indent, ansnum, wdate, r
from (
    select bbsno, wname, title, grpno, indent, ansnum, wdate, rownum as r
    from (
        SELECT bbsno, wname, title, grpno, indent, ansnum, to_char(wdate, 'yyyy-mm-dd') wdate
        FROM bbs
		
			
				
					wname like '%'||#{word}||'%'		
				
				
					title like '%'||#{word}||'%'		
				
				
					content like '%'||#{word}||'%'		
				
				
					title like '%'||#{word}||'%'
					    or content like '%'||#{word}||'%'
				
			
		
        ORDER BY grpno DESC, ansnum ASC
    )
)

where r >= #{sno} and r <= #{eno}

mapper.insert:
insert into
bbs(bbsno, wname, title, content, passwd, wdate, grpno)
values(
    (select nvl(max(bbsno),0)+1 as bbsno from bbs)
    , #{wname}
    , #{title}
    , #{content}
    , #{passwd}
    , sysdate
    , (select nvl(max(grpno),0)+1 as grpno from bbs)
)

mapper.update:
update bbs set
		wname = #{wname}
		, title= #{title}
		, content= #{content}
where bbsno = #{bbsno}

mapper.select:
select bbsno, grpno, title, indent, ansnum
from bbs
where bbsno = #{bbsno}

mapper.update:
update bbs set
	grpno = #{grpno}
	, ansnum = ansnum + 1
where grpno = #{grpno} AND ansnum > #{ansnum}

mapper.insert:
insert into
bbs(bbsno, wname, title, content, passwd, wdate, grpno, indent, ansnum, refnum)
values(
    (select nvl(max(bbsno),0)+1 as bbsno from bbs)
    , #{wname}
    , #{title}
    , #{content}
    , #{passwd}
    , sysdate
    , #{grpno}
    , #{indent}+1
    , #{ansnum}+1
    , #{bbsno}
)

mapper.select:
select count(bbsno) as cnt
from bbs
where bbsno = #{bbsno} and passwd=#{passwd}

SELECT count(*)
FROM bbs
WHERE refnum = #{bbsno}

mapper.update:
update bbs set
viewcnt = viewcnt + 1 
where bbsno = #{bbsno}
